import React,{useState,useEffect} from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'


function UserInfo() {

    const {mobile} = useParams()
    const [users,setUser] = useState({
        id:0,
        name:"",
        mobNumber:"",
        emailId:""
    })

    var re

    useEffect(()=>{
        axios.get("http://localhost:3005/users?mobNumber="+mobile)
        .then(res => {
            console.log(res.data)
            re=res.data
            setUser(res.data)
        })
    },[])

    const loadUser = ()=>{
        axios.get("http://localhost:3005/users?mobNumber="+mobile)
        .then(res=>{
            console.log(res.data)
            setUser(res.data)
        })
    }

    return (
        <div className="container py-4">
            <h1 className="display-4">User Id:  {re.id}</h1>
            <hr />
            <ul className="list-group w-50">
                <li className="list-group-item">name: {users.name}</li>
                <li className="list-group-item">mobile number: {users.mobNumber}</li>
                <li className="list-group-item">email: {users.emailId}</li>
            </ul>
        </div>
    )
}

export default UserInfo
