import React ,{useReducer} from 'react'

const initialState = {
    id: 0,
    userName: "",
    mobNumber: "",
    bookedDate: "",
    movieTime: "",
    status: 0 
}

function reducer(state,{field,value}) {
    return{
        ...state,
        [field]:value
    }
}

function AddTicket() {

    const [state,dispatch] = useReducer(reducer,initialState)

    const onChange = (e) => {
        dispatch({ field:e.target.name, value:e.target.value })
    }

    return (
        <div>
            <h1>Enter Ticket Details</h1>
            <form >
                <label>UserName:
                <input type="text" name="userName" value={state.userName} onChange={onChange}/>
                </label>
                <label>MobileNumber:
                <input type="text" name="mobNumber" value={state.mobNumber} onChange={onChange}/>
                </label>
                <label>BookedDate:
                <input type="text" name="bookedDate" value={state.bookedDate} onChange={onChange}/>
                </label>
                <label>MovieTime:
                <input type="text" name="movieTime" value={state.movieTime} onChange={onChange}/>
                </label>
                <button type="submit">Book Ticket</button>
            </form>
        </div>
    )
}

export default AddTicket
